    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
String ID = jTextField1.getText();
String Name = jTextField2.getText();
String Gender = " ";
      if (jRadioButton1.isSelected()){
          Gender = "Male";
      }else if (jRadioButton2.isSelected()){
          Gender = "Female";
      }

String Address = jTextField3.getText();
String Contact = jTextField4.getText();

if (ID.isEmpty() || Name.isEmpty() || Gender.isEmpty() || Address.isEmpty() || Contact.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please fill all fields .");
            return;
        }
try{
  String url = "jdbc:mysql://127.0.0.1:3306/registration form 2?autoReconnect=true&useSSL=false";
      String user = "Maxwell";
      String password = "Scout";
      Connection con = DriverManager.getConnection(url, user, password);
       String query = "INSERT INTO TABLEFORM (ID, Name, Gender, Address, Contact) VALUES(?, ?, ?, ?, ?) ";
       PreparedStatement pst = con.prepareStatement(query);
       pst.setString(1, ID);
       pst.setString(2, Name);
       pst.setString(3, Gender);
       pst.setString(4, Address);
       pst.setString(5, Contact);
       pst.executeUpdate();
       JOptionPane.showMessageDialog(this,"details are accepted");
       form2 ();
       jTextField1.setText("");
       jTextField2.setText("");
       jRadioButton1.setSelected(false);
       jRadioButton2.setSelected(false);
       jTextField3.setText("");
       jTextField4.setText("");
}
catch (SQLException e) {
            e.printStackTrace();
            JOptionPane.showMessageDialog(this, "Error submitting details:" + e.getMessage());
        }
       
       
    }//GEN-LAST:event_jButton2ActionPerformed

