public void form2 (){
    try 
  {  String url = "jdbc:mysql://127.0.0.1:3306/registration form 2?autoReconnect=true&useSSL=false";
      String user = "Maxwell";
      String password = "Scout";
      Connection con = DriverManager.getConnection(url, user, password);
      String query = "SELECT * FROM tableform";
      Statement stm = con.createStatement();
      ResultSet res = stm.executeQuery(query);
      String columns[] = {"ID", "Name", "Gender","Address","Contact"};
      String data[][] = new String[8][5];
      int i = 0;
      while (res.next() && i< data.length) {
      String ID = res.getString("ID");
     String Name = res.getString("Name");
     String Gender = res.getString("Gender");
     String Address = res.getString("Address");
     String Contact = res.getString("Contact");
        data[i][0] = ID;
        data[i][1] = Name;
        data[i][2] = Gender;
        data[i][3] = Address;  
        data[i][4] = Contact;
       
        i++;
      }
    
  DefaultTableModel model = new DefaultTableModel(data, columns);
  jTable1.setModel(model);
  jTable1.setShowGrid(true);
      jTable1.setShowVerticalLines(true);
  
      
  } catch(SQLException e)
  {
      e.printStackTrace();
    } 
}
